mapper.select:
	SELECT *	
	from (	
select rownum as rn, comm_picture_no, comm_picture_name, comm_picture_write_name,comm_picture_writer, 
				comm_picture_txt, comm_picture_good,comm_picture_view,comm_picture_date
				from (
					select * from comm_picture where comm_picture_write_name LIKE '%'||#{search_input}||'%' order by comm_picture_date desc
				)
			)
		 where rn <= (#{pageNum} * #{amount}) and rn > (#{pageNum} -1) * #{amount}

	SELECT *	
	from (	
select rownum as rn, comm_picture_no, comm_picture_name, comm_picture_write_name,comm_picture_writer, 
				comm_picture_txt, comm_picture_good,comm_picture_view,comm_picture_date
				from (
					select * from comm_picture where comm_picture_writer LIKE '%'||#{search_input}||'%' order by comm_picture_date desc
				)
			)
		 where rn <= (#{pageNum} * #{amount}) and rn > (#{pageNum} -1) * #{amount}

	SELECT *	
	from (	
select rownum as rn, comm_picture_no, comm_picture_name, comm_picture_write_name,comm_picture_writer, 
				comm_picture_txt, comm_picture_good,comm_picture_view,comm_picture_date
				from (
					select * from comm_picture where comm_picture_txt LIKE '%'||#{search_input}||'%' order by comm_picture_date desc
				)
			)
		 where rn <= (#{pageNum} * #{amount}) and rn > (#{pageNum} -1) * #{amount}

	SELECT *	
	from (	
select rownum as rn, cv_no, cv_name, cv_write_name,cv_writer, 
				cv_txt, cv_good,cv_view,cv_date
				from (
					select * from comm_video where cv_write_name LIKE '%'||#{search_input}||'%' order by cv_date desc
				)
			)
		 where rn <= (#{pageNum} * #{amount}) and rn > (#{pageNum} -1) * #{amount}

